import React,{useState} from "react";

const SignupForm=({onLogin})=>{
    const [email, setEmail]=useState('')
    const [password, setPassword]=useState('')
    const [confirmPassword, setConfirmPassword]=useState('');
    const [registeredUsers, setRegisteredUsers]=useState([]);
    const [registered, setRegistered]=useState('')

    const handleSignup=(e)=>{
        e.preventDefault ();
        if (password !== confirmPassword) {
            alert ("Password don't match!");
            return;
        }
        if (registeredUsers.find(user => user.email === email)){
            alert ("Email already registered. Please login.");
            return;
        }

        setRegisteredUsers([...registeredUsers, {email, password}]);

        setEmail('');
        setPassword('')
        setConfirmPassword('');
        setRegistered(true);

    }

    const handleLogin=(e)=>{
        e.preventDefault();

        const user=registeredUsers.find(user => user.email === email && user.password === password);
        if (user) {
            onLogin();
            setEmail('');
            setPassword('')
            setConfirmPassword('')
        } else {
            alert ('Invalid email or password. Please try again.')
        }
    }

    return (
        <div>
            <form onSubmit={registered ? handleLogin : handleSignup}>
                {registered ? <h2>Login</h2> : <h2>Signup</h2>}
                <div>
                    <label>Email:</label>
                    <input
                    type="email"
                     />
                </div>
                <div>
                    <label>password:</label>
                    <input
                    type="password"
                     />
                </div>
                <div>
                    <label>confirmPassword:</label>
                    <input
                    type="password"
                     />
                </div>
            </form>
            <button type="submit" >{registered ? 'Log In' : 'Sign Up'}</button>
            <p onClick={()=> setRegistered(!registered)}>
                {registered ? 'Create an account' : 'Already have an account ? LogIn'}
            </p>
        </div>
    )
}
export default SignupForm;
